import React,{useState} from 'react';

function Folder({explorer}) {
    const [expand, setExpand] = useState(false)
    return(
        <div>
            <span 
            onClick={() => 
            setExpand(!explorer.isFolder)}>
                {explorer.name}
                <br />
            </span>
            <div style={{display: expand? "block": "none" }}>
                {explorer.items 
                ? explorer.items.map(item => (
                    <span>
                        {item.name}
                        <br />
                    </span>))
                : ''
                }
            </div> 
        </div>
    )
}

export default Folder;
